  mois: string[]=[];
  annees: string[]=[];
  currentOccupation: any ={};
  currentContrat: any ={};
  types: string[]=[];
  activites: string[]=[];
  effectues: any[]=[];
  nonEffectues: any[]=[];
  form: any ={};
  produceForm: any = {};
  selectedItem: any ={};
  message?:string;
  existedContrat:any[]=[];
  allsBordereau = 0;
